const chalk = require(`chalk`)
var asdwasdjniuscnac = 1

async function GradRed(prefix, text, intensity){
    var zizi = 21*Math.sin(3* intensity)**2
    var r = Math.round(255 - (12*zizi))
    var g = 0*zizi 
    console.log(chalk.rgb(r, g, 0)(prefix), text)
}

